mapper.select:
SELECT count(user_id)
FROM like_tb
WHERE
	user_id = #{userId}
	AND target_gb = #{targetGb}
	AND target_cd = #{targetCd}


SELECT NVL(COUNT(user_id), 0)
FROM like_tb
WHERE
	target_gb = #{targetGb}
	AND target_cd = #{targetCd}

mapper.update:
UPDATE like_tb
SET
	status = #{status},
	modified_dt = CURRENT_TIMESTAMP
WHERE
	user_id = #{userId}
	AND target_gb = #{targetGb}
	AND target_cd = #{targetCd}

mapper.delete:
DELETE
FROM
	like_tb
WHERE
	target_gb = #{targetGb}
	AND target_cd = #{targetCd}
